import * as echarts from 'echarts';
import markerIcon from '@/assets/imgs/icons8-圈向上-64.png';
export default {
  name: 'FarmMonitor',
  // 避免使用HTML保留标签作为组件名
  data() {
    return {
      // 核心数据
      weather: {
        date: `${new Date().getMonth() + 1}月${new Date().getDate()}日`,
        weekday: ['周日', '周一', '周二', '周三', '周四', '周五', '周六'][new Date().getDay()],
        weather: '晴',
        tempMin: 18,
        tempMax: 30,
        windDir: '东风',
        windForce: '3级'
      },
      currDate: new Date().toLocaleDateString(),
      temp: 22,
      hum: 50,
      tempHistory: [{
        time: '00:00',
        value: 22
      }, {
        time: '00:05',
        value: 22.5
      }],
      humHistory: [{
        time: '00:00',
        value: 50
      }, {
        time: '00:05',
        value: 49.5
      }],
      tempRange: {
        min: 18,
        max: 26
      },
      humRange: {
        min: 40,
        max: 60
      },
      // 新增WebSocket和Worker相关状态
      mainWs: null,
      mapWs: null,
      backendUrl: 'ws://8.152.103.136:9090/ws/data',
      // 设备数据
      water: {
        status: 0,
        value: 0
      },
      weight: 50,
      devices: [{
        name: '水泵',
        status: 0
      }, {
        name: '电机',
        status: 0
      }, {
        name: '灯光',
        status: 0
      }, {
        name: '阀门',
        status: 0
      }, {
        name: '加热',
        status: 0
      }, {
        name: '风机',
        status: 0
      }],
      // 连接状态
      mainConn: false,
      lastUpdate: '-',
      maxHistory: 10,
      // 进度条相关配置
      barHeight: 120,
      // 进度条高度
      colors: ['#43a047', '#8bc34a', '#cddc39'],
      // 进度条渐变颜色

      // 地图相关
      lnglat: '',
      course: '',
      mapStatus: '地图加载中...',
      mapConn: false,
      map: null,
      marker: null,
      // 图表实例
      tempChart: null,
      humChart: null
    };
  },
  computed: {
    // 计算属性（Vue 2 中使用 function 语法）
    weightPercent() {
      return this.weight;
    },
    tempWarn() {
      return this.temp;
    },
    humWarn() {
      return this.hum;
    },
    waterAlarm() {
      return this.water.value;
    },
    gradientBg() {
      return `linear-gradient(to top, ${this.colors.join(', ')})`;
    }
  },
  methods: {
    // 图表初始化与渲染
    initCharts() {
      const tempDom = document.getElementById('tempChart');
      const humDom = document.getElementById('humChart');
      if (!tempDom || !humDom) return;
      this.tempChart = echarts.init(tempDom);
      this.humChart = echarts.init(humDom);
      window.addEventListener('resize', () => {
        this.tempChart?.resize();
        this.humChart?.resize();
      });
      this.renderCharts();
    },
    renderCharts() {
      // 温度图表配置
      this.tempChart.setOption({
        tooltip: {
          trigger: 'axis'
        },
        grid: {
          left: '3%',
          right: '4%',
          bottom: '3%',
          containLabel: true
        },
        xAxis: {
          type: 'category',
          data: this.tempHistory.map(i => i.time),
          axisLabel: {
            rotate: 30
          }
        },
        yAxis: {
          type: 'value',
          name: '温度(°C)',
          min: Math.min(...this.tempHistory.map(i => i.value), this.tempRange.min) - 2,
          max: Math.max(...this.tempHistory.map(i => i.value), this.tempRange.max) + 2,
          markLine: {
            data: [{
              yAxis: this.tempRange.min,
              name: '最低阈值',
              lineStyle: {
                color: 'red'
              }
            }, {
              yAxis: this.tempRange.max,
              name: '最高阈值',
              lineStyle: {
                color: 'red'
              }
            }]
          }
        },
        series: [{
          name: '温度',
          type: 'line',
          data: this.tempHistory.map(i => i.value),
          smooth: true,
          lineStyle: {
            color: '#e74c3c'
          },
          itemStyle: {
            color: '#e74c3c'
          },
          areaStyle: {
            color: new echarts.graphic.LinearGradient(0, 0, 0, 1, [{
              offset: 0,
              color: 'rgba(231,76,60,0.3)'
            }, {
              offset: 1,
              color: 'rgba(231,76,60,0)'
            }])
          }
        }]
      });

      // 湿度图表配置
      this.humChart.setOption({
        tooltip: {
          trigger: 'axis'
        },
        grid: {
          left: '3%',
          right: '4%',
          bottom: '3%',
          containLabel: true
        },
        xAxis: {
          type: 'category',
          data: this.humHistory.map(i => i.time),
          axisLabel: {
            rotate: 30
          }
        },
        yAxis: {
          type: 'value',
          name: '湿度(%)',
          min: Math.min(...this.humHistory.map(i => i.value), this.humRange.min) - 5,
          max: Math.max(...this.humHistory.map(i => i.value), this.humRange.max) + 5,
          markLine: {
            data: [{
              yAxis: this.humRange.min,
              name: '最低阈值',
              lineStyle: {
                color: 'blue'
              }
            }, {
              yAxis: this.humRange.max,
              name: '最高阈值',
              lineStyle: {
                color: 'blue'
              }
            }]
          }
        },
        series: [{
          name: '湿度',
          type: 'line',
          data: this.humHistory.map(i => i.value),
          smooth: true,
          lineStyle: {
            color: '#3498db'
          },
          itemStyle: {
            color: '#3498db'
          },
          areaStyle: {
            color: new echarts.graphic.LinearGradient(0, 0, 0, 1, [{
              offset: 0,
              color: 'rgba(52,152,219,0.3)'
            }, {
              offset: 1,
              color: 'rgba(52,152,219,0)'
            }])
          }
        }]
      });
    },
    // 历史数据更新
    updateHistory(ref, val, time) {
      ref.push({
        time,
        value: val
      });
      if (ref.length > this.maxHistory) ref.shift();
    },
    // 地图初始化
    initMap() {
      const loadMap = () => new Promise((res, rej) => {
        if (window.AMap) return res(window.AMap);
        const script = document.createElement('script');
        script.src = 'https://webapi.amap.com/maps?v=1.4.15&key=你的高德Key'; // 替换为你的高德Key
        script.onload = () => res(window.AMap || rej(new Error('地图加载失败')));
        script.onerror = () => rej(new Error('地图脚本加载失败'));
        document.head.appendChild(script);
      });
      loadMap().then(AMap => {
        this.map = new AMap.Map('map', {
          resizeEnable: true,
          zoom: 16,
          center: [116.397470, 39.908823],
          viewMode: '2D'
        });
        this.marker = new AMap.Marker({
          position: [116.397470, 39.908823],
          icon: new AMap.Icon({
            size: new AMap.Size(32, 32),
            image: markerIcon,
            imageSize: new AMap.Size(64, 64),
            imageOffset: new AMap.Pixel(-16, -16)
          }),
          anchor: 'center'
        });
        this.map.add(this.marker);
        this.map.on('complete', () => this.mapStatus = '地图已就绪');
        this.map.on('error', () => this.mapStatus = '地图渲染错误');
      }).catch(err => {
        this.mapStatus = '地图加载失败';
        console.error(err);
      });
    },
    // 数据刷新
    // 新增: 初始化SharedWorker
    initWebSocket() {
      // 主数据WebSocket连接
      this.mainWs = new WebSocket(this.backendUrl);
      this.mainWs.onopen = () => {
        console.log('主数据WebSocket连接成功');
        this.mainConn = true;
      };
      this.mainWs.onmessage = event => {
        console.log('Received WebSocket message:', event.data);
        try {
          const data = JSON.parse(event.data);
          console.log('Parsed data:', data);
          if (data.type === 'MAIN_DATA') {
            console.log('Handling MAIN_DATA:', data.payload);
            this.handleMainData(data.payload);
          } else if (data.type === 'MAP_DATA') {
            console.log('Handling MAP_DATA:', data.payload);
            this.handleMapData(data.payload);
          }
        } catch (e) {
          console.error('Failed to parse WebSocket message:', e, 'Raw data:', event.data);
        }
      };
      this.mainWs.onerror = error => {
        console.error('WebSocket错误:', error);
        this.mainConn = false;
        this.mapConn = false;
      };
      this.mainWs.onclose = () => {
        console.log('WebSocket连接关闭，尝试重连...');
        this.mainConn = false;
        this.mapConn = false;
        setTimeout(() => this.initWebSocket(), 3000);
      };
    },
    // 新增: 处理Worker消息
    handleWorkerMessage(message) {
      switch (message.type) {
        case 'CONNECTED':
          this.mainConn = true;
          this.mapConn = true;
          break;
        case 'DISCONNECTED':
          this.mainConn = false;
          this.mapConn = false;
          break;
        case 'ERROR':
          console.error('WebSocket错误:', message.data);
          this.mainConn = false;
          this.mapConn = false;
          break;
        case 'MAIN_DATA':
          this.handleMainData(message.data);
          break;
        case 'MAP_DATA':
          this.handleMapData(message.data);
          break;
        default:
          console.log('未知消息类型:', message.type);
      }
    },
    // 修改: 数据刷新方法
    refresh() {
      if (this.mainWs && this.mainWs.readyState === WebSocket.OPEN) {
        this.mainWs.send(JSON.stringify({
          type: 'REFRESH_DATA'
        }));
        this.lastUpdate = new Date().toLocaleString();
      }
    },
    // 处理主数据更新的回调函数
    handleMainData(data) {
      try {
        console.log('从后端接收到的主数据:', data);
        this.temp = data.tem || data.T || this.temp;
        console.log('温度数据:', this.temp);
        this.hum = data.hum || data.H || this.hum;
        console.log('湿度数据:', this.hum);
        this.water = {
          status: data.waterStatus || 0,
          value: data.Wal || data.waterlevel || 0
        };
        console.log('水位数据:', this.water);
        this.weight = Math.max(0, Math.min(100, parseFloat(data.weight) || 0));
        console.log('重量数据:', this.weight);
        this.hum = data.hum || data.H || this.hum;
        this.water = {
          status: data.waterStatus || 0,
          value: data.Wal || data.waterlevel || 0
        };
        this.weight = Math.max(0, Math.min(100, parseFloat(data.weight) || 0));

        // 更新设备状态
        this.devices.find(d => d.name === '水泵').status = data.pump || 0;
        console.log('水泵状态:', this.devices.find(d => d.name === '水泵').status);
        this.devices.find(d => d.name === '电机').status = data.motor || 0;
        console.log('电机状态:', this.devices.find(d => d.name === '电机').status);
        this.devices.find(d => d.name === '灯光').status = data.light || 0;
        console.log('灯光状态:', this.devices.find(d => d.name === '灯光').status);
        this.devices.find(d => d.name === '阀门').status = data.door || 0;
        console.log('阀门状态:', this.devices.find(d => d.name === '阀门').status);
        this.devices.find(d => d.name === '加热').status = data.heat || 0;
        console.log('加热状态:', this.devices.find(d => d.name === '加热').status);
        this.devices.find(d => d.name === '风机').status = data.fan || 0;
        console.log('风机状态:', this.devices.find(d => d.name === '风机').status);

        // 更新历史数据
        const timeStr = new Date().toLocaleTimeString();
        this.updateHistory(this.tempHistory, this.temp, timeStr);
        this.updateHistory(this.humHistory, this.hum, timeStr);
        this.lastUpdate = new Date().toLocaleString();
        this.currDate = new Date().toLocaleDateString();
        this.renderCharts();
        this.mainConn = true;
      } catch (e) {
        this.mainConn = false;
        console.error('处理主数据失败:', e);
      }
    },
    // 处理地图数据更新的回调函数
    handleMapData(data) {
      try {
        if (data.lng !== undefined && data.lat !== undefined) {
          const lng = parseFloat(data.lng),
            lat = parseFloat(data.lat);
          if (!isNaN(lng) && !isNaN(lat) && lng >= -180 && lng <= 180 && lat >= -90 && lat <= 90) {
            this.lnglat = `${lng.toFixed(6)},${lat.toFixed(6)}`;
            this.marker?.setPosition([lng + 0.01, lat - 0.001]);
            this.map?.panTo([lng + 0.01, lat - 0.001], {
              duration: 300
            });
          }
        }
        if (data.cogt !== undefined) {
          const angle = parseFloat(data.cogt);
          if (!isNaN(angle)) {
            this.course = `${angle.toFixed(1)}°`;
            this.marker?.setRotation((angle % 360 + 360) % 360);
          }
        }
        this.mapConn = true;
      } catch (e) {
        this.mapConn = false;
        console.error('处理地图数据失败:', e);
      }
    },
    // 处理连接状态变化
    handleConnectionStatus(status) {
      this.mainConn = status;
      this.mapConn = status;
    }
  },
  mounted() {
    // Vue 2 中使用 $nextTick
    this.$nextTick(() => {
      this.initCharts();
      this.initMap();

      // 初始化SharedWorker
      this.initWebSocket();
    });
  },
  beforeDestroy() {
    // 清理Worker连接
    if (this.mainWs) {
      this.mainWs.close();
      this.mainWs = null;
    }
    if (this.mapWs) {
      this.mapWs.close();
      this.mapWs = null;
    }

    // 清理图表和地图
    if (this.tempChart) this.tempChart.dispose();
    if (this.humChart) this.humChart.dispose();
    if (this.map) this.map.destroy();
  }
};
